var queue = [];
var texts = [];
var x = 0;
var y = 0;
var speed = 2;
var xdir = 1;
var ydir = 1;
// var moveToX = 0;
// var moveToY = 0;
var which = 0;

function setup() {
  createCanvas(400, 400);
  queue.push([200, 200]);
  texts.push("200, 200");
  //textAlign(CENTER, CENTER);
  textSize(18);
}

function draw() {
  background(220);
  text("Click to add queue points", 16, 16);

  x += xdir * speed;
  y += ydir * speed;

  if (x > queue[which][0] + 1) {
    xdir = -1;
  } else if (x < queue[which][0] - 1) {
    xdir = 1;
  } else {
    addToWhich();
  }

  if (y > queue[which][1] + 1) {
    ydir = -1;
  } else if (y < queue[which][1] - 1) {
    ydir = 1;
  } else {
    addToWhich();
  }

  ellipse(x, y, 10, 10);
  for(var i = 0; i < texts.size; i++) {
    for(var j = 0; j < queue.size; j++) {
      fill(0);
      text(texts[i],queue[j][0],queue[j][1]);
    }
  }
  //console.log(texts);
}

function addToWhich() {
  if (which >= queue.length - 1) {
    which = 0;
  } else {
    which++;
  }
}

function mousePressed() {
  // moveToX = mouseX;
  // moveToY = mouseY;
  queue.push([mouseX, mouseY]);
  texts.push(mouseX + ", " + mouseY);
  //console.log(queue[0][1]);
}

let tri = [600,700,800,650,700,750];
let bigTri = [-200, -400, 0];

let drawn = false;

function setup() {
  createCanvas(600, 600);
}

function draw() {
  background(255);
  noStroke();
  fill(0);

  triangle(bigTri[0], 100, bigTri[1], 500, bigTri[2], 500);
  // triangle(300, 100, 100, 500, 500, 500);

  if (bigTri[2] >= 500) {
    bigTri[0] = 300;
    bigTri[1] = 100;
    bigTri[2] = 500;
  } else {
    for(i=0; i<3; i++) {
      bigTri[i] += 5;
    }
  }

  fill(255);
  triangle(tri[0], 300, tri[1], 500, tri[2], 300);
  triangle(tri[3], 400, tri[4], 500, tri[5], 400);
  if(tri[3] <=350) {
    tri[3] = 350;
    tri[4] = 400;
    tri[5] = 450;
  }

  if(tri[0] <=200) {
    tri[0] = 200;
    tri[1] = 300;
    tri[2] = 400;
    drawn = true;
  } else {
    for(i=0; i<6; i++) {
      tri[i] -= 4;
    }
  }

  if (drawn == true) {
    let seconds = millis() / 2000.0;
    let s = sin(seconds * TWO_PI);
    fill(s < 0 ? "red" : "blue" );
    // ellipse(100, 100, abs(s) * 100, 100);
    triangle(400, 300, (abs(s))*(350-250)+350, 400, 450, 400);
    triangle(350, 400, (abs(s))*(300-200)+300, 500, 400, 500);
    triangle(450, 400, (abs(s))*(400-300)+400, 500, 500, 500);
  } 
}

