    myTurtle.moveTo(spiderX, spiderY);
    myTurtle.penDown();
    drawSpider(spiderX,spiderY,90);
    spiderX+=xspeed;
    spiderY+=yspeed;
    if (spiderX>=width || spiderX-30<=0) {
        xspeed*=-1;
    }
    if (spiderY+50>=height || spiderY-10<=0) {
        yspeed*=-1;
    }
}

function drawSpider(x,y,r) {

    myTurtle.turnTo(r);
    for(var a = 0; a < 360; a++) {
        myTurtle.turnRight(1);
        myTurtle.moveForward(.25);
    }
    myTurtle.moveForward(50);
    myTurtle.moveForward(-50);
    myTurtle.penUp();
    myTurtle.turnRight(90);
    myTurtle.moveForward(29);
    myTurtle.penDown();
    myTurtle.turnTo(r);
    myTurtle.moveForward(50);
    myTurtle.moveForward(-35);
    myTurtle.penUp();
    myTurtle.turnRight(-90);
    myTurtle.moveForward(9);
    myTurtle.penDown();
    myTurtle.turnTo(r);
    myTurtle.moveForward(25);
    myTurtle.moveForward(-25);
    myTurtle.penUp();
    myTurtle.turnRight(-90);
    myTurtle.moveForward(9);
    myTurtle.penDown();
    myTurtle.turnTo(r);
    myTurtle.moveForward(25);
    myTurtle.penUp();
    myTurtle.moveTo(x-17,y-5)
    myTurtle.penDown();
    for(var b = 0; b < 360; b++) {
        myTurtle.turnRight(1);
        myTurtle.moveForward(.05);
    }
    myTurtle.penUp();
    myTurtle.moveTo(x-5,y-5)
    myTurtle.penDown();
    for(var c = 0; c < 360; c++) {
        myTurtle.turnRight(1);
        myTurtle.moveForward(.05);
    }
}

