function draw() {
  background(255);

  for (x = 0; x < img.width; x++) {
    for (y = 0; y < img.height; y++) {
      let in_color = img.get(x, y);
      let next_color = img.get(x+10,y);
      let r = red(in_color);
      let g = green(in_color);
      let b = blue(in_color);

      if (lightness(in_color) > lightness(next_color)) {
        img.set(x+5,y,color(0,255-g,0));
      }

      if (r > 250) {
        let rand = random(1);
        if(rand < .5) {
          img.set(x,y,color(0));
        } else {
          img.set(x,y,color(255))
        }

      }

      // if (r > 200) {
      //   img.set(x + 1, y, g);
      // }

      img.updatePixels();
    }
  }

  image(img, 0, 0, width, height);
  noLoop();
}

