function effervesce(xPos, yPos, mod) {
  myTurtle.penUp();
  myTurtle.moveTo(xPos, yPos);
  myTurtle.penDown();
  for (let j = 0; j < 180; j += 1) {
    myTurtle.moveForward(frameCount % mod);
