  }
}

var amplitude_slider, freq_slider, speed_slider, change_shape;

var startX = 50;
var startY = 250;
var endX = 450;
var endY = 50;
var vel = 0;


function setup() {
  createCanvas(500, 300);
  rectMode(CENTER);

  createP('Amplitude');
  amplitude_slider = createSlider(0, 100, 20);
  createP('Frequency');
  freq_slider = createSlider(0, 100, 20);
  createP('Speed');
  speed_slider = createSlider(0, 100, 20);
  change_shape = createCheckbox('Change to Rectangle', false);

}


function draw() {
  background(50);
  ellipseMode(CENTER);

  var amplitude = amplitude_slider.value() / 100;
  var freq = freq_slider.value();
  var speed = speed_slider.value() / 100;


  noiseDetail(1, 0.5);



  fill(255);
  noStroke();

  // study this loop. do you understand how the line of ellipses is created?

  vel += speed;
  for (i = 0; i < 1; i += 0.02) {
    var x = lerp(startX, endX, i);
    var y = lerp(startY, endY, i);

    // hint: drive offsetX and offsetY with noise() instead of random()
    var offsetX = noise(freq * i + vel) * amplitude * 100;
    var offsetY = noise(freq * i + vel, 10) * amplitude * 100;



    if (change_shape.checked()) {
      rect(x + offsetX, y + offsetY, 10, 10);
    } else {
      ellipse(x + offsetX, y + offsetY, 10, 10);
    }
  }
}

