// returns the RGBA values of the pixel at x, y in the img's pixels[] array
// returns values as an array [r, g, b, a]
// use instead of p5s built in .get(x,y), for much better performance (more than 1000x better in many cases)
// see: http://p5js.org/reference/#/p5/pixels[]
// we don't need to worry about screen pixel density here, because we are not reading from the canvas

function getQuick(img, x, y) {
  let i = (y * img.width + x) * 4;
  return [
    jfaImage.pixels[i],
    jfaImage.pixels[i + 1],
    jfaImage.pixels[i + 2],
    jfaImage.pixels[i + 3]
  ];
}

function hogChange(img) {
  img.loadPixels();

  for (let y = 0; y < img.height; y += 3) {
    for (let x = 0; x < img.width; x += 3) {
      let noiseR = noise(x);
      let rVal = map(noiseR, 0, 1, 0, 255);
      let noiseG = noise(y);
      let gVal = map(noiseG, 0, 1, 0, 255);
      let noiseB = noise(x, y);
      let bVal = map(noiseB, 0, 1, 0, 255);
      let c = getQuick(img, x, y);
      c = [rVal - c[0], gVal - c[1], bVal - c[2], c[3]];
      img.set(x, y, c);
    }
  }
  img.updatePixels();
}

