            tempsprite.onMousePressed = function(pressedsprite) {
                testForWin(pressedsprite);
            }
        }
    }

}

function testForWin(testsprite) {
    if (!hasWon) {
        testsprite.remove();
        let isWin = false;
        if (answerGroup.length == problemGroup.length) {
            for (let i = 0; i < problemGroup.length; i++) {
                if (answerGroup[i].position.x - 400 == problemGroup[i].position.x &&
                    answerGroup[i].position.y == problemGroup[i].position.y) {
                    isWin = true;
                } else {
                    isWin = false;
                    break;
                }
            }
        }
        if (isWin) {
            console.log('win');
            hasWon = true;
        }
    }
}


function draw() {
    background(50, 50, 80);

    //instructions
    fill(255);
    textSize(36);
    if (hasWon) {
        text("congratulations\nreload to play again", width/2, height-80);
    } else {
        text("chip away at the right block until\nyou get the same shape as the left block\nif you make a mistake, you have to\nrestart by reloading the sketch", width/2, height-160);
    }

    drawSprites();
}

