function Chart(startY, r, g, b, amplitude, s) {

  ellipseMode(CENTER);
  noStroke();
  fill(r, g, b);

  var frequency = frequency_slider.value();
  var speed = timeSpeed_slider.value() / 1000;

  move+=speed

  for(var i = 0; i < 1; i+= 0.01) {
