// Generate an image from scratch: pixel by pixel. Dont call any high-level drawing function like ellipse() or rect().
let colors = [];

function setup() {
  createCanvas(500, 500);
  colors[0] = color(255, 0, 0); //red
  colors[1] = color(255, 127, 0); //orange
  colors[2] = color(255, 255, 0);
  colors[3] = color(0, 255, 0);
  colors[4] = color(0, 0, 255);
  // colors[5] = color(46, 43, 95);
  colors[5] = color(139, 0, 255);
  // colors[6] = color(139, 0, 255);
  colors[6] = color(0, 0, 0); //black
}

function draw() {
  background(255);

  img = createImage(20, 20);
  img.loadPixels();

  for (var y = 0; y < img.height; y++) {
    for (var x = 0; x < img.width; x++) {
      img.set(x, y, random(200, 255));
    }
  }

  drawOutline();
  drawColors();
  drawLetters();

    img.updatePixels();

    noSmooth();
    image(img, 0, 0, width, height);
    noLoop();
  }

  function drawOutline() {

    let c = colors[6];

    for (var i = 4; i < 8; i++) {
      img.set(i, 3, c);
      if (i == 4) {
        img.set(i - 1, 4, c);
      }
    }

    for (var i = 11; i < 15; i++) {
      let c = color(0, 0, 0);
      img.set(i, 3, c);
      if (i == 11) {
        img.set(i - 3, 4, c); // middle
        img.set(i - 1, 4, c); // middle
        img.set(i - 2, 5, c); // middle
      }
      if (i == 14) {
        img.set(i + 1, 4, c); // right side
        for (let j = 5; j < 9; j++) {
          img.set(i + 2, j, c); // right side
          img.set(i - 12, j, c); // left side
        }
      }
    }

    for (let i = 11; i < 26; i++) {
      if (i > 18) {
        img.set(i - 9, 34 - i, c); // right bottom
      } else {
        img.set(i - 9, i - 2, c); // left bottom
      }
    }
  }

  function drawColors() {
    let r = colors[0]; //red
    for (let i = 3; i < 16; i++) {
      if (i == 3 || i == 8 || i == 10 || i == 15) {
        img.set(i, 5, r);
      } else if (i == 9) {} else {
        img.set(i, 4, r);
        img.set(i, 5, r);
      }
    }

    let o = colors[1] //orange
    for (let i = 3; i < 16; i++) {
      img.set(i, 6, o);
      img.set(i, 7, o);
    }

    let yellow = colors[2]; //yellow
    for (let i = 3; i < 16; i++) {
      img.set(i, 8, yellow);
      img.set(i, 9, yellow);
    }

    let g = colors[3]; //green
    for (let i = 4; i < 15; i++) {
      if (i == 4 || i == 14) {
        img.set(i, 10, g);
      } else {
        img.set(i, 10, g);
        img.set(i, 11, g);
      }
    }

    let b = colors[4]; //blue
    for (let i = 6; i < 13; i++) {
      if (i == 6 || i == 12) {
        img.set(i, 12, b);
      } else {
        img.set(i, 12, b);
        img.set(i, 13, b);
      }
    }

    let p = colors[5] //purple;
    for (let i = 8; i < 11; i++) {
      if (i == 8 || i == 10) {
        img.set(i, 14, p);
      } else {
        img.set(i, 14, p);
        img.set(i, 15, p);
      }
    }
  }

function drawLetters() {
let c = colors[6];

  for (let i = 6; i < 10; i++) { // L
    img.set(4, i, c);
  }

  for (let i = 6; i < 8; i++) { // O
    for (let j = 8; j < 10; j++)
      img.set(i, j, c);
  }

  for (let i = 7; i < 10; i++) { // v
    img.set(9, i, c);
  }

  for (let i = 10; i < 12; i++) { // v
    img.set(i, 19 - i, c);
    if (i == 11) {
      img.set(i, 18 - i, c);
    }
  }

  for (let i = 13; i < 15; i++) {
    if (i == 13) {
      for(let j = 5; j < 10; j++) {
        img.set(13, j, c);
        }
      }
      img.set(i, 5, c);
      img.set(i, 7, c);
      img.set(i, 9, c);
    }
}

