function drawColor(x, y) {
  var n = noise(x * inc, y * inc);
  if (n < 0.2) {
    return fill(color(157, 100, 100)); //tallest
  } else if (n < 0.4) {
    return fill(color(100, 100, 100)); //tree
  } else if (n < 0.6) {
    treasure(x, y);
    return fill(color(53, 100, 100)); //land
  } else if (n < 0.7) {
    return fill(color(200, 100, 100)); //water
  }
}

