  myTurtle.penUp();
  myTurtle.moveTo(250, 150);
  myTurtle.penDown();
  strokeWeight(8);

  let wavelength = 49100 / 1110;
  colorMode(HSB);
  var hue = 0;
  var rNum = 1000;
  var rCount = 0;
  var lCount = 0;
  for (var i = 0; i < 49100; i++) {
    var noiseVar = noise(xOff) * rNum;
    if (i % 100 === 0) {
