let jfaInput;

function preload() {
  jfaInput = loadImage('https://upload.wikimedia.org/wikipedia/en/8/82/Blatant_Localism.jpg');
}

function setup() {
  createCanvas(500, 485);
  jfaInput.loadPixels();
  noLoop();
}

function draw() {
  background(0);

  noStroke();
  jfaProcess(0.5);
}

function getQuick(img, x, y) {
  let i = (y * img.width + x) * 4;
  return [
    jfaInput.pixels[i],
    jfaInput.pixels[i + 1],
    jfaInput.pixels[i + 2],
    jfaInput.pixels[i + 3]
  ];
}

function jfaProcess(dotMultiplier) {
  let space = 500 / jfaInput.width;
  for (let x = 0; x < jfaInput.width; x += 1) {
    for (let y = 0; y < jfaInput.height; y += 1) {
      // let dotMultiplier = 3;
      fill(random(255), random(255), random(255));
      let pix = getQuick(jfaInput, x, y);
      let dotSize = (lightness(pix) / 255) * dotMultiplier;
      ellipse(
        x * space + space * 0.5,
        y * space + space * 0.5,
        dotSize,
        dotSize
      );
    }
  }
  if (dotMultiplier <= 2) {
    dotMultiplier += 1;
    jfaProcess(dotMultiplier);
  }
}

