let gridSize = 10
let gridWidth;
let gridHeight;
let waterColor;
let landColor;
let yellowColor;
let land = []
let freq = 0.15;
let xMark;
let redColor;

function setup() {
  createCanvas(400, 400);
  gridWidth = width / gridSize;
  gridHeight = height / gridSize;
  waterColor = color(41, 173, 255);
  landColor = color(255, 163, 0);
  redColor = color(255, 0, 77);
  yellowColor = color(255, 236, 39);
  for (x = 0; x < gridWidth; x++) {
    land[x] = []
    for (y = 0; y < gridWidth; y++) {
      let mask = (
        1 -
        dist(x, y, gridWidth / 2, gridHeight / 2) / 2 / gridSize);
      if (mask < 0.1) {
        mask = 0
      }
      let n = noise(x * freq, y * freq);
      let type = (n * mask) / 2;

      if (type > 0.13) {
        type = yellowColor;
      }
      else if (type > 0.1) {
        type = landColor;
      } else {
        type = waterColor;
      }
      // type=color(mask*255)
      // print(mask)
      land[x][y] = type
    }
  }
  while (!xMark) {
    let test = createVector(
      floor(random(gridWidth)),
      floor(random(gridHeight))
    )
    if (land[test.x][test.y] == yellowColor) {
      xMark = test
    }
  }
}

function draw() {
  background(220);
  noStroke();
  for (x = 0; x < gridWidth; x++) {
    for (y = 0; y < gridWidth; y++) {
      fill(land[x][y])
      rect(x * gridSize, y * gridSize, gridSize, gridSize)
    }
  }
  textAlign(CENTER)
  stroke(redColor)
  fill(redColor)
  strokeWeight(3)
  textSize(gridSize * 2)
  text("X", xMark.x * gridSize + gridSize / 2, xMark.y * gridSize + gridSize / 2)
}

