  }

  stroke(random(255), random(255), random(255));
  strokeWeight(5);

  for (var i = 1; i < n; i++) {
    myTurtle.turnRight(-100-n);
    myTurtle.moveForward(40);
    myTurtle.turnRight(150+n);
    myTurtle.moveForward(40);
  }
}

