    var start = millis();

    // loop over every x,y pixel coordinate in the image
    for (x = 0; x < faceImg.width; x++) {
        for (y = 0; y < faceImg.height; y++) {


            var pixelRed = getQuick(faceImg, x, y)[0];
            var pixelGreen = getQuick(faceImg, x, y)[1];
