                line(x,y,x+10,y+2);

            }else if (100 > pixelGreen){
                fill(0);
               noStroke();
                point(x, y);
            }else{
              fill(50);
                noStroke();
               point(x, y);
            }
        }
    }

    var end = millis();

    console.log(`took ${floor(end - start)} ms`);

    noLoop();
}


function getQuick(img, x, y) {

    var i = (y * img.width + x) * 4;
    return [
        faceImg.pixels[i],
        faceImg.pixels[i + 1],
        faceImg.pixels[i + 2],
        faceImg.pixels[i + 3],
    ];
}

