var color_picker;
var size_slider;
var target_slider;

function setup() {
  createCanvas(640, 300);
  rectMode(CENTER);
  createP('Color');
  color_picker = createInput("#FFFFFF", "color");
  createP('Size');
  size_slider = createSlider(12, 36, 18);
  is_target = createCheckbox("Make Into Targets", false);
  createP('Target Spacing');
  target_slider = createSlider(5, 10, 6);
  noisy_targets = createCheckbox("Make Into Noisy Targets", false);
  frameRate(6);
}


function draw() {
  background(50);
  ellipseMode(CENTER);
  var frequency = 100;
  var amplitude = 10;
  noiseDetail(.5);

  fill(color_picker.value());

  noStroke();
  for (var x = 0; x < width; x += .5) {
    let n = noise(x * frequency);
    n = n * amplitude * height;
    if (is_target.checked()) {
      for(let i = size_slider.value(); i > 0; i-=target_slider.value()){
        stroke(255, 0, 0);
        ellipse(x, n, i, i);
      }
    } else if (noisy_targets.checked()) {
      for(let i = size_slider.value(); i > 0; i-=target_slider.value()){
        let m = noise(i, millis());
        m = map(m, 0, 1, 12, 36);
        stroke(255, 0, 0);
        ellipse(x, n, m, m);
      }
    } else {
      ellipse(x, n, size_slider.value(), size_slider.value());
    }
  }

}

