function overCircle(x,y,r){
  if (dist(x,y,mouseX,mouseY)<r){
    return false;
  } else {
    return true;
  }
}

// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.5.14/p5.js
// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.5.14/addons/p5.dom.js
// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.6.0/addons/p5.sound.js

let mySound;
let fft;
const buffer = new Float32Array(44100);

function setup() {
  createCanvas(400, 400);
  background(50);


  fft = new p5.FFT(0.8, 128);
  fft.setInput(mySound);

  //retro future
  let wavelength = 44100 / random(200, 600);
  for (let i = 0; i < 44100; i++) {
    if (i % 5000 === 0) {
      wavelength = 44100 / random(0, 600);
      fill(255);
      //ellipse(wavelength, 100, 10, 10);
    }
    buffer[i] = sin((i / wavelength) * 2 * PI);
  }
  mySound = new p5.SoundFile();
  mySound.setBuffer([buffer]);
  var text = createP('Click to generate a tune!');
  text.style('font-family','Courier New');
  text.style('margin-left','70px');
}

function draw() {
  const data = fft.analyze();
  console.log(data.length);
  colorMode(RGB);
  background(0,25);
  noStroke();

  for (let i = 0; i < 15; i++) {
    const x = map(i, 0, 15, 0, 400);
    const y = map(data[i], 0, 255, 390, 10);
    colorMode(HSB);
    var size = map(data[i],0,255,4,30);
    fill(255-data[i],255,255);
    ellipse(x, y, size, size);
  }
}

function mousePressed() {
  let wavelength = 44100 / random(200, 600);
  var xOff = 0.0;
  for (let i = 0; i < 44100; i++) {
    if (i % 4000 === 0) {
      var noiseVar = noise(xOff) * 50;
      wavelength = 80000 / (mouseX + mouseY)/2 + noiseVar;
    }
    buffer[i] = sin((i / wavelength) * 2 * PI);
    xOff += 0.01;
  }
  mySound = new p5.SoundFile();
  mySound.setBuffer([buffer]);
  mySound.play(0, 1, 1);
}

