const storyGrammar = {
    'top': ['#div#'],
    'div': ['<div style="color: #color#; text-align: center; margin-top: 30%; text-decoration:#td#; text-transform: capitalize;  font-family: #font#; font-size: 50px;">#content#</div>'],
    'content': ['#div#', '#word#'],
