        for(var adj=5;adj<=10;adj+=0.5){
            index++
            x[index] = cx + r * Math.cos(a+adj)
            y[index] = cy + r * Math.sin(a+adj)
        }
        points[index] = new Point(x[index], y[index])
    }
}
