let x = 40;
let y = 50;
let done = false;
let finished = false;

let numbers = [];
let numbersDone = [];

function setup() {
  createCanvas(600, 600);
  background(255);
  for(i=1;i<70;i++) {
    numbers.push(new Num(i));
  }
}

function draw() {

  fill(0);

  let randomNum = floor(random(0,70));


  for(i=0;i<numbers.length;i++) {
    if(finished == false) {
      if(randomNum == i) {
        numbers[i].update();
        fill(255,0,0);
        noStroke();
        ellipse(numbers[i].x+10,numbers[i].y-5,numbers[i].chosen);
          if(numbers[i].chosen >= 50) {
            if (!numbers[i].finished) {
              numbersDone.push(numbers[i]);
            }
            numbers[i].done();
            fill(255,255,0);
            ellipse(numbers[i].x+10,numbers[i].y-5,numbers[i].chosen);
            if(numbersDone.length >= 6) {
            finished = true;

            }  
          }


        }
    }

    if (done == false) {
      numbers[i].drawNumbers(x,y);
      x+= 62;
        if(x > width-50) {
          x = 40;
          y+=70;
        }
      }

    if(numbersDone.length >= 6 && numbers[i].finished == true) {
    numbers[i].drawNumbers(numbers[i].x,numbers[i].y);
    }

  }

  done = true;

  drawText();


}

class Num {
  constructor(number) {
    this.n = number;
    this.chosen = 0;
    this.x = 0;
    this.y = 0;
    this.finished = false;
  }

  drawNumbers(x,y) {
    fill(0);
    textSize(20);
    text(this.n,x,y);
    this.x = x;
    this.y = y;
  }

  update() {
    this.chosen += 5;
  }

  done() {
    this.finished = true;
  }

}

function drawText() {
  fill(255,0,0);
  textSize(18);
  text("Your Lucky Numbers!!", width-200, height-60);
}

