function technicolorSnow(imageSize, ySpace, xSpace) {
  const img = createImage(imageSize, imageSize);
  img.loadPixels();
  for (let y = 0; y < img.height; y += ySpace) {
    var noiseNum = noise(xOff) *420;
    var noiseNumY = noise(yOff) *250;
    for (let x = 0; x < img.width; x += xSpace) {
      const c = color((y+ noiseNum) % 360, 360, 360);
      img.set(noiseNum, noiseNumY, c);
      yOff +=0.002;
    }
    xOff += 0.01;
  }
  img.updatePixels();
  noSmooth();
  image(img, 0, 0, width, height);
}

