const maincharacters = ["Harry", "Susan", "Justin", "Helen"];
const antagonists = ["Jerry", "Christopher", "Sally", "Fran"]
// let mc = maincharacters[Math.random(0, maincharacters.length-1)];
// let ant = antagonists[Math.random(0, antagonists.length-1)];
let mc = maincharacters[1];
let ant = antagonists[0];

const storyGrammar = {
    "story": [`It was a dark and stormy night. ${mc} couldn't #presenttenseverb#. #incitingincident# #risingaction# #climax# #resolution#`],
    "incitingincident": [`Suddenly, ${ant} #pasttenseverb# the #object#!`, `Suddenly, ${ant} #pasttenseverb# the #adjective# #object#!`],
    "risingaction": [`Because of this, ${mc} #pasttenseverb#.`, `This caused ${mc} to #presenttenseverb#.`, `${mc}, now #adjective#, #pasttenseverb#.`, `${mc} immediately #pasttenseverb# a #subject#.`, `${mc} immediately #pasttenseverb# a #adjective# #subject#.`],
    "climax": [`${mc} #pasttenseverb# the #object# to reveal that...`, `${mc} #pasttenseverb# a #adjective# #object# to reveal that...`, `Although #adjective#, ${mc} managed to trick ${ant} into revealing a secret: `],
    "resolution": [`${ant} was the #object# all along!`, `${ant} #pasttenseverb# a #subject#!`, `${ant} had been hiding a #subject#!`, `${ant} had been hiding a #adjective# #subject#!`, `${ant} was now #adjective#!`],
    "subject": ["lightning bolt", "monkey", "child", "subway train", "melody"],
    "object": ["house", "river", "chimney", "lawn", "storm", "icon"],
    "adjective": ["old", "friendly", "infuriated", "sleepy", "red", "shaky", "gross"],
    "presenttenseverb": ["sleep", "ride a bike", "run for office", "read", "play"],
    "pasttenseverb": ["searched", "struck", "ran around", "grabbed", "ate", "sang"]
}


function main() {
    let grammar = tracery.createGrammar(storyGrammar);
    let story = grammar.flatten("#story#");

    const storyDiv = document.createElement('div');
    storyDiv.style = "font-size: 30px; margin: 10%; line-height: 1.5;";
    storyDiv.textContent = story;

    document.body.insertAdjacentElement("beforeend", storyDiv);
}


setTimeout(main, 10);

