  for (var i = 0; i < 270; i++) {
    var angle1 = map(i, 0, 270, 0, PI);
    var radius1 = 270 * noise(i * 0.009, t * 0.003);
    var x1 = radius1 * cos(angle1);
    var y1 = radius1 * sin(angle1);
    curveVertex(x1*2.1, y1*1.6);
  }
  endShape(CLOSE);

   beginShape();
  for (var q = 0; q < 180; q++) {
    var angle2 = map(q, 0, 180, 0, PI);
    var radius2 = 270 * noise(q * 0.018, t * 0.005);
    var x2 = radius2 * cos(angle2);
    var y2 = radius2 * sin(angle2);
    curveVertex(y2*1.8, x2*2.4);
  }
  endShape(CLOSE);

   beginShape();
  for (var p = 0; p < 240; p++) {
    var angle3 = map(p, 0, 240, 0, PI*1.8);
    var radius3 = 240 * noise(p * 0.027, t * 0.002);
    var x3 = radius3 * cos(angle3);
    var y3 = radius3 * sin(angle3);
    curveVertex(y3*0.72, x3*0.63);
  }
  endShape(CLOSE);

  t += 0.81;



}

