  img.pixels[i + 1]=c
  img.pixels[i + 2]=c
  // img.pixels[i + 3]=c
  // img.pixels[i]=c[0]
  // img.pixels[i + 1]=c[1]
  // img.pixels[i + 2]=c[2]
  // img.pixels[i + 3]=c[3]
}

function pget(x, y) {
  var i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3]
  ];
}

