var rad = 30;

var circle1 = new Path.Circle({
  center: [200, 250],
  radius: rad,
  fillColor: 'red',
});

var circle2 = new Path.Circle({
  center: [280, 250],
  radius: rad,
  fillColor: 'green',
});

var deltaPosition1 = new Point(-5, 0);
var deltaPosition2 = new Point(5, 0);

function onFrame() {
  var dx = Math.abs(circle1.position.x - circle2.position.x);

  circle1.position += deltaPosition1;
  circle2.position += deltaPosition2;

  if (circle1.position.x === 25) {
    deltaPosition1.x = Math.abs(deltaPosition1.x);
  }
  if (dx <= 55) {
    deltaPosition1.x = -Math.abs(deltaPosition1.x);
    deltaPosition2.x = Math.abs(deltaPosition2.x);
    circle1.fillColor = 'yellow';
    circle2.fillColor = 'purple';
  } else {
    circle1.fillColor = 'red';
    circle2.fillColor = 'green';
  }
  if (circle2.position.x > 425) {
    deltaPosition2.x = -Math.abs(deltaPosition2.x);
  }
}

