let rSlider;
let gSlider;
let bSlider;
let xSpeedSlider;
let ySpeedSlider;
let squareAnimationCheck;

function setup() {
  createCanvas(windowWidth, windowHeight);
  createP('RGB controls, left to right.');
  rSlider = createSlider(0, 1, 0.01, 0.01);

  gSlider = createSlider(0, 1, 0.001, 0.01);

  bSlider = createSlider(0, 1, 0.05, 0.01);

  createP('Rate of X and Y noise.');
  xSpeedSlider = createSlider(0, 1, 0.1, 0.01);

  ySpeedSlider = createSlider(0, 1, 0.5, 0.01);
  backgroundAnimationCheck = createCheckbox('Animate Background', false);
  squareAnimationCheck = createCheckbox('Animate Squares', true);
}

function draw() {
  let valR = rSlider.value();
  let valG = gSlider.value();
  let valB = bSlider.value();
  let xSpeed = xSpeedSlider.value();
  let ySpeed = ySpeedSlider.value();
  let noiseFillR;
  let noiseFillG;
  let noiseFillB;
  let noiseX;
  let noiseY;
  let posX;
  let posY;
  let noiseSizeX;
  let noiseSizeY;
  if (backgroundAnimationCheck.checked()) {
    noiseFillR = noise(frameCount * valR) * 255;
    noiseFillG = noise(frameCount * valG) * 255;
    noiseFillB = noise(frameCount * valB) * 255;
    background(noiseFillB, noiseFillR, noiseFillG);
    noiseX = noise(frameCount * xSpeed);
    posX = map(noiseX, 0, 1, 0, 100);
    noiseY = noise(frameCount * ySpeed);
    posY = map(noiseY, 0, 1, 0, 50);
  } else {
    noiseFillR = noise(valR) * 255;
    noiseFillG = noise(valG) * 255;
    noiseFillB = noise(valB) * 255;
    background(noiseFillB, noiseFillR, noiseFillG);
    noiseX = noise(xSpeed);
    posX = map(noiseX, 0, 1, 0, 100);
    noiseY = noise(ySpeed);
    posY = map(noiseY, 0, 1, 0, 50);
  }
  noStroke();
  fill(noiseFillR, noiseFillG, noiseFillB);
  for (let x = 0; x < width; x += posX) {
    for (let y = 0; y < height; y += posY) {
      if (squareAnimationCheck.checked()) {
        noiseSizeX = noise(frameCount * 0.1) + 10;
        noiseSizeY = noise(frameCount * 0.9) + 10;
        rect(x, y, noiseSizeX, noiseSizeY);
      } else {
        noiseSizeX = noise(0.1) + 10;
        noiseSizeY = noise(0.9) + 10;
        rect(x, y, noiseSizeX, noiseSizeY);
      }
    }
  }
}

