function moving_triangles(){
   let motion = frameCount * speed;
  for (let x = -gridSize; x < width / gridSize; x++) {
    for (let y = -gridSize; y < height / gridSize; y++) {
      let faces = []

      for (let i = 0; i < 4; i++) {
        let offset = createVector(x + dirxB[i], y + diryB[i]);
        let n = noise(offset.x * frequency + motion, offset.y * frequency + motion);
        faces.push(createVector(offset.x + n, offset.y + n));
      }
      for (let i = 0; i < 2; i++) {
        fill(59, 20, noise(x + i, y + i) *255)
        triangle(
          faces[i].x * gridSize,
          faces[i].y * gridSize,
          faces[i + 1].x * gridSize,
          faces[i + 1].y * gridSize,
          faces[i + 2].x * gridSize,
          faces[i + 2].y * gridSize
        );
      }
    }
  } 
}

