let flockSize = 200;
class Boid {
  constructor() {
    this.pos = createVector(random(width), random(height));
    this.v = p5.Vector.random2D();
    // this.v=createVector(0,1)
    this.a = createVector();
    this.los = 50;
    this.maxForce = 1;
    this.maxSpeed = 4;
    this.size = 10
  }
  draw() {
    // strokeWeight(16);
    // stroke(0)
    // line(this.pos.x,this.pos.y,this.pos.x+this.v.x*100,this.pos.y+this.v.y*100)
    push()
    translate(this.pos)
    rotate(this.v.heading())
    let size = this.size;
    // rotate(5)
    // triangle(-size/2,-size/2,size/2,-size/2,0,size)
    fill(colorPicker.value())
    triangle(size / 2, 0, -size / 2, size / 2, -size / 2, -size / 2)


    // ellipse(this.pos.x, this.pos.y, 10)
    // ellipse(0,0, 10)

    pop()
  }
  draw_range() {
    noStroke()
    fill(0, 20)
    push()
    translate(this.pos.x, this.pos.y)
    ellipse(0, 0, this.los)
    // fill(255,0,255)
    let size2 = this.size *2

    let alignment = this.align().mult(size2);
    let cohesion = this.cohesion().mult(size2);
    let seperation = this.seperation().mult(size2);
    alignment.mult(alignSlider.value())
    cohesion.mult(cohesionSlider.value())
    seperation.mult(seperationSlider.value())
    stroke(255, 0, 0)
    line(0, 0, alignment.x, alignment.y)
    stroke(0, 255, 0)
    line(0, 0, cohesion.x, cohesion.y)
    stroke(0, 0, 255)
    line(0, 0, seperation.x, seperation.y)
    pop()
  }
  update() {
    this.pos.add(this.v)
    this.pos.x = this.pos.x % width
    this.pos.y = this.pos.y % height
    this.v.add(this.a)
    this.a.mult(0)
  }

  flock() {
    this.los = rangeSlider.value()
    this.maxForce=forceSlider.value()
    this.maxSpeed=speedSlider.value()
    let alignment = this.align();
    let cohesion = this.cohesion();
    let seperation = this.seperation();
    alignment.mult(alignSlider.value())
    cohesion.mult(cohesionSlider.value())
    seperation.mult(seperationSlider.value())
    // this.a=alignment
    this.a.add(alignment)
    this.a.add(cohesion);
    this.a.add(seperation);
  }
  align() {
    let steering = createVector();
    if (this.local.length > 0) {
      for (let other of this.local) {
        steering.add(other.v)
      }
      steering.div(this.local.length);
      steering.setMag(this.maxSpeed);
      steering.sub(this.v)
      steering.limit(this.maxForce)
    }
    return steering
  }
  cohesion() {
    let steering = createVector();
    if (this.local.length > 0) {
      for (let other of this.local) {
        steering.add(other.pos)
      }
      steering.div(this.local.length);
      steering.sub(this.pos)
      steering.setMag(this.maxSpeed);
      steering.sub(this.v)
      steering.limit(this.maxForce)
    }
    return steering
  }
  seperation() {
    let steering = createVector();
    if (this.local.length > 0) {
      for (let other of this.local) {
        let diff = p5.Vector.sub(this.pos, other.pos)
        diff.div(p5.Vector.dist(other.pos, this.pos))
        steering.add(diff)
      }
      steering.div(this.local.length);
      steering.setMag(this.maxSpeed);
      steering.sub(this.v)
      steering.limit(this.maxForce)
    }
    return steering
  }
  find_local(boids) {
    this.local = []
    for (let other of boids) {
      if (other != this && p5.Vector.dist(other.pos, this.pos) < this.los) {
        this.local.push(other)
      }
    }
  }
}


