const storyGrammar = {
    "Story": ["It was a dark and stormy night. #SceneDescriptor#. #FirstAction#. #SecondAction#. #ThirdAction#. #End#"],
    "SceneDescriptor": ["The wind was #Adjective1# and the #Animals# were #State#. On the #Location#, a single light shone"],
    "FirstAction": ["As if appearing from out of nowhere, a #Age# #Person2# starts #Verb#ing towards the light"],
    "SecondAction": ["Just a few #Measurement# away from it now, she #Verb#s into a #Thing# and lets out a #Adjective1# #Sound#"],
    "ThirdAction": ["The light goes out, a #Noun# #Action# and the dark shadow of a #Person1# #Verb#s towards the #Location#"],
    "End": ["He had been found.", "He had gotten away yet again.", "It was the second time he had gotten away."],
    "Adjective1": ["piercing", "strong", "loud", "wild", "fierce"],
    "Animals": ["coyotes", "owls", "leaves", "snakes", "wolves"],
    "State": ["restless", "hooting", "howling", "rustling", "hunting"],
    "Location": ["edge of the water", "edge of the forest", "far end of the valley", "opposite side of the field", "hill just over the valley"],
    "Age": ["middle-aged", "young", "tired-looking, old", "teenaged", "five year old"],
    "Person1": ["man", "person", "boy"],
    "Person2": ["woman", "person", "girl"],
    "Verb": ["stumble", "walk", "skulk", "tiptoe", "run"],
    "Measurement": ["yards", "feet", "steps", "paces"],
    "Thing": ["wheelbarrow", "fence", "car", "sign", "pole"],
    "Sound": ["scream", "yell", "cry", "yelp", "grunt"],
    "Noun": ["door", "window"],
    "Action": ["slams", "creaks", "swings open"]
}


function main() {
    let grammar = tracery.createGrammar(storyGrammar);
    let story = grammar.flatten("#Story#");

    document.body.style = "background: rgb(0,0,0); background: linear-gradient(180deg, rgba(0,0,0,1) 0%, rgba(90,103,158,1) 100%)";

    const storyDiv = document.createElement('div');
    storyDiv.style = "font-size: 24px;font-family: 'Palatino'; margin: 10% 5%; padding: 7%; line-height: 1.5; color: white; text-align: justify";
    storyDiv.textContent = story;


    document.body.insertAdjacentElement("beforeend", storyDiv);
}


setTimeout(main, 10);

