let tiles = [];
const tileNum = 24;
const canvasWidth = 310;
const canvasHeight = 310;
function setup() {
  createCanvas(canvasWidth, canvasHeight);
  for (let i = 0; i < tileNum; i++) {
    if (i <= 5) {
      let xPos = i * 52;
      let col = i * 52;
      tiles[i] = new Tile(xPos, 0, 50, 50, col);
    } else if (i >= 6 && i < 12) {
      let xPos = (i - 6) * 52;
      let col = map(xPos, 255, 0, 0, 255);
      tiles[i] = new Tile(xPos, 52, 50, 50, col);
    } else if (i >= 12 && i < 18) {
      let xPos = (i - 12) * 52;
      let col = (i - 12) * 52;
      tiles[i] = new Tile(xPos, 104, 50, 50, col);
    } else {
      let xPos = (i - 18) * 52;
      let col = map(xPos, 255, 0, 0, 255);
      tiles[i] = new Tile(xPos, 156, 50, 50, col);
    }
  }
}

function draw() {
  background(255, 255, 255);
  for (let i = 0; i < tileNum; i++) {
    tiles[i].display();
  }
}

class Tile {
  constructor(x, y, width, height, col) {
    this.x = x;
    this.y = y;
    this.width = width;
    this.height = height;
    this.col = col;
  }
  display() {
    fill(this.col);
    rect(this.x, this.y, this.width, this.height);
  }
}

