function draw() {
  clear();
  background(75);
  fill('white');
  serpTri(10, 310, 300);
}

function tri(x1, y1, x2, y2, x3, y3) {
  triangle(x1, y1, x2, y2, x3, y3);
}

function serpTri(x, y, length) {
  const x1 = x;
  const y1 = y;
  const midpoint = length / 2 + x;
  const x2 = midpoint;
  const halfLeng = 0.5 * length;
  const square = sqrt(sq(length) - sq(halfLeng));
  const y2 = y - square;
  const x3 = length + x;
  const y3 = y1;
  triangle(x1, y1, x2, y2, x3, y3);
  const x4 = midpoint / 2 + x1 / 2;
  const y4 = y1 - (y1 - y2) / 2;
  const x5 = midpoint;
  const y5 = y1;
  const x6 = midpoint / 2 + x3 / 2;
  const y6 = y4;
  triangle(x4, y4, x5, y5, x6, y6);
  //   console.log(x4, y6);
}

int cols; 
int rows;

