void setup() {
  size(600,600,P3D);
  //scale = int(random(10,80));
  scale = 15;
  println(scale);

  cols = w/scale;
  rows = h/scale;

  landscape = new float[cols][rows];

}

void draw() {
  background(255);
  //frameRate(10);

  noStroke();
  //stroke(0);
  fill(73, 128, 81, 60);

  lights();


  //move+= 0.03;
  //yOffset = move;

  for(int j = 0; j < rows; j++) {
    float xOffset = 0; 
    for(int i = 0; i < cols; i++) {
      landscape[i][j] = map(noise(xOffset, yOffset), 0, 1, -70, 150);
      xOffset+= 0.1;
    }
    yOffset +=0.1;
  }
  translate(width/2, height/2);
  rotateX(PI/3);
  translate(-w/2, -h/2);

  for(int j = 0; j < rows-1; j++) {
    beginShape(TRIANGLE_STRIP);

    for(int i = 0; i < cols; i++) {
      vertex(i*scale, j*scale, landscape[i][j]);
      vertex(i*scale, (j+1)*scale, landscape[i][j+1]);
    }
    endShape();
  }
  noLoop();



}

