var amplitude_slider;

var startX = 50;
var startY = 250;
var endX = 450;
var endY = 50;
let n;
var t =0 ;

function setup() {
    createCanvas(500, 300);
    colorMode(HSB)
    createP('Amplitude');
    amplitude_slider = createSlider(-50, 200, 50);
    createP('Frequency');
    freq_slider = createSlider(0, 255, 50);
    createP('Speed');
    octave_slider = createSlider(0, 255, 50);

}

function draw() {
    background(255);
    ellipseMode(CENTER);

    var amplitude = amplitude_slider.value() / 1000;
    let frequency = freq_slider.value() / 1000;
    var speed = octave_slider.value()/1000;
    var speed2 = -frameCount*speed

    noiseDetail(1, .5);

    fill(255);
    noStroke();

    for (i = 0; i < 1; i += .02) {
        var x = lerp(startX, endX, i);
        var y = lerp(startY, endY, i);
        var s = sin(2)*i*-200
        var s2 = pow(sin(2),3)*50
        t+=0.1
        //console.log(t)
        n = noise(x*frequency + speed2) - 0.5;
        // console.log()
        fill(i*t, 100, 100)
        var offsetX = (n) * amplitude * 1000;
        var offsetY = (n) * amplitude * 1000;
        ellipse(x+offsetX, y+offsetY, 10+s, 10+s);
        ellipse(x+offsetX+s, y+offsetY+s, 10+s2, 10+s2);
        ellipse(x+offsetX+s2, y+offsetY+s, 10+s, 10+s);
        if(t>=1000){
            t=0;
        }
    }
}

