module tower() {
    color("gray") {
        hull() {
            cylinder(h=0.1, d=4.75);
            translate([0, 0, 10]) {
                cylinder(h=1, d=4.25);
            }
        };
        difference() {
            difference() {
                hull() {
                    translate([0, 0, 10]) {
                        cylinder(h=1, d=4.25);
                    }
                    translate([0, 0, 11]) {
                        cylinder(h=3, d=6);
                    }
                }
                translate([0,0,11.1]) cylinder(h=4, d=4.5);
            }
            for(i=[0:2]) {
                translate([0,0,14.5]) {
                    rotate([0, 0, i*60]) cube([1.25, 7, 3], true);
                }
            }
        }
    }
}

module wall(x, y, z) {
    color("gray") {

        translate([0, -y/2, 0]) cube([x, y+2, 2]);
        difference() {
            difference() {
                union() {
                    translate([0, -y/2, z-2]) cube([x, y+2, 2]);
                    hull() {
                            cube([x, y, 0.1]);
                            translate([0,y/8,z-.1]) cube([x, y-.5, 0.1]);
                        }
                    }
                for(i=[-x/32:5:x]){
                    translate([i, -y, z-1]) cube([2,y*3,2]);
                }
            }
            translate([0, 0, z-1.9]) cube([x, y, 3]);
        }
    }
}

module castle() {
    hull() {
        translate([0,-20,25]) cylinder(h=.1, r=.2);
        translate([0,-20,23]) cylinder(h=.1, r=.5);
    }
    hull() {
        translate([0,-20,20]) cylinder(h=.1, r=1.5);
        translate([0,-20,23]) cylinder(h=.1, r=.5);
    }
    hull() {
        translate([0,-20,20]) cylinder(h=.1, r=1.5);
        translate([0,-20,17]) cylinder(h=.1, r=3);
    }
    color("black") translate([0, -20, 0]) cylinder(h=17, r=2);
}

