const storyGrammar = {
    "story": ["It was a dark and stormy night, #who# #what#, at #where# during #when# by #how#"],
    "who": ["#subject.a#"],
    "what":["#verb# #object#"],
    "where":["#adjective.a# #place#"],
    "when":["a pandemic","lunch time","brunch","a movie","sunrise","break time","emergency escape","crucial time","a trying time","a funeral","a walk","commiting a crime", "a strom","a bath","a fight"],
    "how":["#verb2# #ob2.a# #adverb# "],
    "subject": ["#adjective# #noun#"],
    "noun": ["Gaston","Jafar", "Hades","Evil Queen","Scar","Yzam","Stepmother","Creulla","Ursula","Magneto","Ultron","Kingpin","Venom"],
    "adverb": ["exceptionally","agressively","willingly","shockingly","potencially","intentionally","slowly","gently","painfuly","forcefully","wrongfully"],
    "adjective": ["crazy", "psychotic", "insane", "dark", "comical", "cynical","loud","disgusting","warm","golden","gentle","light","heavy","dense","dirty","gloomy","sensual","sad","pathetic","self-loathing"],
    "object": ["Joe Exotic", "Carole Baskin", "Jeff Lowe", "Dr.Antle","Howard Baskin","Don","John Finlay","Rick Krikham", "James Garretson"],
    "verb": ["ate","threw","kissed","judged","picked up","orderd","changed","slapped","dragged","punched","caressed","touched","fought","texted","called","watched","stared","licked","burnt","looked","shouted"],
    "verb2":["licking","touching","texting","throwing","calling","fighting","looking","starting","yelling","singing","caressing","slapping","poking","flipping"],
    "ob2":["cake","car","phone","water","corn","table","hand","it","him","that","this","them","siblings","fire"],
    "place":["church","beach","alley","palace","bathroom","kitched","backyard","crime scene","public bath","mall","ocean","closet","park","basement","garage","pool"]
}


function main() {
    let grammar = tracery.createGrammar(storyGrammar);
    let story = grammar.flatten("#story#");

    const storyDiv = document.createElement('div');
    storyDiv.style = "font-size: 30px; margin: 10%; line-height: 1.5;";
    storyDiv.textContent = story;

    document.body.insertAdjacentElement("beforeend", storyDiv);
}


setTimeout(main, 10);

