function brownianMotion(x, y, clusterArea, clusterSize) {
  for (let point = 0; point < clusterSize; point++) {
    let nextX, nextY;

    nextX = x + random(-clusterArea, clusterArea);
    nextY = y + random(-clusterArea, clusterArea);

    ellipse(x, y, 5, 5);

    x = constrain(nextX, 0, width - 5);
    y = constrain(nextY, 0, height - 5);
  }
}

