var amplitude_slider, speed_slider, frequency_slider;

var startX = 50;
var startY = 250;
var endX = 450;
var endY = 50;


function setup() {
    createCanvas(500, 300);


    createP('Amplitude');
    amplitude_slider = createSlider(0, 10, 1);
    createP('Freqency');
    frequency_slider = createSlider(0, 200, 100);
    createP('Speed');
    speed_slider = createSlider(0,100, 0);


}


function draw() {
    background(50);
    ellipseMode(CENTER);

    var amplitude = amplitude_slider.value() ;
    var frequency = frequency_slider.value()/120 ;
    var speed = speed_slider.value()/1000;


    noiseDetail(1, .5);



    fill(255);
    noStroke();

    // study this loop. do you understand how the line of ellipses is created?
    for (i = 0; i < 1; i += .02) {
        var x = lerp(startX, endX, i);
        var y = lerp(startY, endY, i);

        // hint: drive offsetX and offsetY with noise() instead of random()
        var offsetX = noise(frameCount * speed*0.1  * random(frequency))* amplitude * 10;
        var offsetY = noise(frameCount * speed*0.15 * random(frequency))* amplitude * 10;

        ellipse(x + offsetX, y + offsetY, 10, 10);
    }



}

