let x = 0;
let y = 0;
let space = 40;

function setup() {
  createCanvas(400, 400);
  background(0);
}

function draw() {
  stroke(255);
  let randNum = random(4);
  strokeWeight(3);
  stroke(255);
  if (randNum <1) {
    figure1();
  } else if (randNum <2) {
    figure2();
  } else if (randNum <3) {
    figure3();
  } else if (randNum <4) {
    figure4();
  }

  x = x + space;

  if( x > width) {
  x = 0;
  y+=space;
  }

}

function figure1() {
  line(x,y+10,x+10,y);
  line(x,y+20,x+20,y);
  line(x,y+30,x+30,y);
  line(x,y+40,x+40,y);
  line(x+10,y+40,x+40,y+10);
  line(x+20,y+40,x+40,y+20);
  line(x+30,y+40,x+40,y+30);

}

function figure2() {
  line(x,y+30,x+10,y+40);
  line(x,y+20,x+20,y+40);
  line(x,y+10,x+30,y+40);
  line(x,y,x+40,y+40);
  line(x+10,y,x+40,y+30);
  line(x+20,y,x+40,y+20);
  line(x+30,y,x+40,y+10);
}

