function onFrame(event) {
    for (var i = 0, l = children.length; i < l; i++) {
        var item = children[i];
        var delta = (mousePoint - item.position) / (i);
        item.rotate(i / 100);
        // item.scale(mousePoint.x/1000);
        if (delta.length > 0.000001)
            item.position += delta;
    }
}

// face generator
let slider1;
let slider2;
let eyeSize;
let smileSize;

function setup() {
  createCanvas(400, 400);
  // eye value
  slider1 = createSlider(0, 80, 40);
  slider1.position(80, 10);

  slider2 = createSelect();
  slider2.position(80, 30);
  slider2.option('small');
  slider2.option('medium');
  slider2.option('large');
}

function draw() {
  background(220);
  fill(0);
  textSize(15);
  strokeWeight(0);
  text("eye size", 10, 25);
  text("smile size", 10, 45);

  fill(255, 255, 0);
  stroke(0);
  strokeWeight(2);
  ellipse(width/2, height/2, 200, 200);

  //eyes
  eyeSize = slider1.value();
  fill(255);
  strokeWeight(1);
  ellipse(150, 150, 80, eyeSize);
  ellipse(250, 150, 80, eyeSize);

  fill(0);
  ellipse(150, 150, 50, eyeSize);
  ellipse(250, 150, 50, eyeSize);

  //mouth
  noFill();
  strokeWeight(3);
  let diam = 170;
  if (slider2.value() == 'small') {
    smileSize = 0.2;
  } else if (slider2.value() == 'medium') {
    smileSize = 0.5;
  } else if (slider2.value() == 'large') {
    smileSize = 0.9;
  }
  let start = .1 * PI
  let end = .9 * PI
  let smileDiam = smileSize * diam;
  arc(width / 2, height / 2, smileDiam, smileDiam, start, end);
}

