// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.5.14/p5.js
var xOff = 0.0;
var yOff = 0.0;
var zOff = 0.0;
var noiseVarX,noiseVarY, noiseVarZ;
function setup() {
    createCanvas(700, 700);
}

function draw() {
    background(0);

    img = createImage(50, 50);
    img.loadPixels();

    for (var y = 0; y < img.height; y++) {
                  noiseVarY = noise(yOff) * 502;
        for (var x = 0; x < img.width; x++) {
            noiseVarX = noise(xOff) * 215 + random(102);
            noiseVarZ = noise(zOff) * 802;

            var c = color(noiseVarX, noiseVarY, noiseVarZ);
            img.set(x, y, c);
            zOff +=0.05;
        }
        xOff += 0.01;
        yOff +=0.02;
    }

    img.updatePixels();

    noSmooth();
    image(img, 0, 0, width, height);
    noLoop();

}

let servo_angle;
let angleSlider;

function setup() {
  createCanvas(400, 400);
  angleMode(DEGREES);

  angleSlider = createSlider(0, 180, 60);
  angleSlider.position(60, 10);
  angleSlider.style("width", "100");


}

function draw() {
  background(0);

  drawSliderText();

  fill(31, 117, 255,200);
  rect(150, 165, 100, 70);

  strokeWeight(2);
  stroke(0);
  ellipse(width/2, height/2, 25, 25);

  translate(width/2, height/2);
  rotate(-120);




  servo_angle = angleSlider.value();

  strokeWeight(15);
  noFill();


  push();
  rotate(servo_angle);
  stroke(255);
  line(0, 0, 75, 0);
  pop();

  //drawSliderText();

}

