let vid;
let playing = false;
let button;
let mySound;

function preload() {
  mySound = loadSound('https://adamoore.net/assets/compFormSoundSketch1.wav');
  vid = createVideo('https://adamoore.net/assets/sound1.mp4');
}

function setup() {
  //   createCanvas(500, 500);
  noCanvas();
  vid.size(400, 200);
  button = createButton('play');
  button.mousePressed(toggleVid);
}

function toggleVid() {
  if (playing) {
    vid.pause();
    mySound.pause();
    button.html('play');
  } else {
    vid.loop();
    mySound.loop(0, 1, 1);
    button.html('pause');
  }
  playing = !playing;
}

