function brush(x, y) {
  ellipse(x, y, random(5, 9), random(5, 9));
  ellipse(x + random(-4, 4), y + random(-4, 4), 8, 2);
  ellipse(x + random(-4, 4), y + random(-4, 4), 6, 2.5);
  ellipse(x + random(-4, 4), y + random(-4, 4), 4.5, 7);
  ellipse(x + random(-4, 4), y + random(-4, 4), 3, 7);
}

function pick(clrs) {
  const index = floor(random(clrs.length));
  return clrs[index];
}

function drawRect(x, y, h) {
  let a;

  tMar = random(8, 35);
  sMar = random(6, 58) + x;

  for (let col = sMar; col < width - sMar; col += 4) {
    for (let row = tMar + y; row < h + tMar + y; row += 4) {
      if (h / 2 - abs(h / 2 - row + tMar + y) < 30) {
        a = (h / 2 - abs(h / 2 - row + tMar + y)) / 30;
      } else if ((width - sMar * 2) / 2 - abs(width / 2 - col) < 30) {
        a = ((width - sMar * 2) / 2 - abs(width / 2 - col)) / 30;
      } else {
        a = 0.6;
      }
      fill(hue, s, v, a);
      brush(col, row);
    }
  }
  brush(width / 2, height / 2);
}

