function getQuick(img, x, y) {
  if (x < 0 || x >= img.width || y < 0 || y >= img.height) return
  var i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}

function setQuick(img, x, y, c) {
  if (x < 0 || x >= img.width || y < 0 || y >= img.height) return
  var i = (y * img.width + x) * 4;
  for (let f = 0; f < 4; f++) {
    img.pixels[f + i] = c[f];
  }
}

function compareQuick(a, b) {
  for (let i = 0; i < 3; i++) {
    if (a[i] != b[i]) {
      return false;
    }
  }
  return true;
}

#define NUM 20.0

//IQ isosceles triangle
//https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdTriangleIsosceles( in vec2 p, in vec2 q )
{
    p.x = abs(p.x);
    vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float s = -sign( q.y );
    vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot(b,b), s*(p.y-q.y)  ));
    return -sqrt(d.x)*sign(d.y);
}
//basic random 
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //scroling vertically
    fragCoord += vec2(0,iTime*10.);

    // Scale the sapce
    float scale = NUM;

    //a weird hacky thing to offset the rows
    float xOff = iResolution.x / NUM * .5; //half a cell width
    vec2 c = floor ( fragCoord.xy * NUM/ iResolution.xy); //c row value
    float offMag = xOff*step(rand(vec2(c.y,0.)),.5);// how much to offset the ro

    //apply row offset and also apply the scale
    vec2 uv = (fragCoord+vec2(offMag,0.))/iResolution.xy * scale;

    //then do the uv transforms
    vec2 cell = floor(uv);
    vec2 cellUV = fract(uv);

    bool hasTri = (rand(cell) > .1);//will have a triangle if greater than .2
    //get orientation of the cell
    float triUp = float((rand(cell) > .5)); // will be 1 if up, 0 if down

    //now compare if the cell above us is opposite direction, if so, then we swap
    //and only if there's a triangle there, otherwise we don't care!
    float foundOpposingTri = 0.;
    for (int i = -1; i <= -1; i ++ ){
        if (i == 0) {continue;}
        if (triUp != float(rand(cell + vec2(0.,i)) > .5)){
            foundOpposingTri = 1.;
        }
    }
    triUp = (foundOpposingTri - triUp);

    //calculate triangle based on up or down
    vec2 tri;
    float triangle;

    if (triUp > .999){
    	tri = vec2(.5,-1.0);
        triangle = smoothstep(.0001,.00,sdTriangleIsosceles ( cellUV - vec2(.5,1.), tri));

    }else{
        tri = vec2( .5, 1.0);
        triangle = smoothstep(.0001,.00, sdTriangleIsosceles ( cellUV - vec2(.5,0.), tri));

    }

    //randomly color
    float colorSwitch = rand(cell+vec2(234.,-2352.));
    vec3 col; //background green
    //if has tri
    if (hasTri){
        if (colorSwitch > .5 && triangle > .9){
            col = vec3(.765, .698,.745) * triangle;
        }else{
            col = vec3(.545, .592,.576) * triangle;
        }
    }
    if (length(col) < .001) {col = vec3(.094, .73, .6);}

    // Output to screen
    fragColor = vec4(col,1.0);
}

