function start() {
    mic.loop();
}

function stop() {
    mic.stop();
}


function draw() {
  translate(width/2, height/2);
  background(20,20,200);
  let spectrum = fft.analyze();


  beginShape();
  for (i = 0; i < spectrum.length; i++) {
    // vertex(i, map(spectrum[i], 0, 255, height, 0));

    var r = map(spectrum[i], 0, 255, height/5, 0);
    ellipse(r*sin(i), r*cos(i), 1,1)
  }
  endShape();
}

