function setup() {
  createCanvas(1280, 720);
  colorMode(HSB, 1);
}

function draw() {
  let bg = color(0.08, 0.3, 0.4);
  background(bg);

  //bg gradient
  for (i = 0; i < width; i++) {
    let bgg = lerpColor(
      color(0.04, 0.9, 0.9),
      bg,
      (dist(i, 0, width / 2, 0) / (width - 200)) * 2
    );
    stroke(bgg);
    line(i, 0, i, height);
  }

  //first group
  let step;
  let a;
  for (let y = 150; y < height - 350; y += step) {
    if (dist(0, y, 0, height / 2) > 100) {
      step = 2 + 2 * noise(y);
    } else {
      step = 1 + noise(y * 2);
    }
    a = 1 - (dist(0, y, 0, height / 2 - 50) / (height - 100)) * 2;
    for (let x = 0; x < width; x++) {
      let center = color(0.03, 1, 1);
      let c = lerpColor(
        center,
        bg,
        (dist(x, y, width / 2, y) / width) *
          2 *
          ((height + 300) / dist(x, y, x, height + 400))
      );
      c.setAlpha(a);
      strokeWeight(0.5);
      stroke(c);
      point(x, y);
    }
  }

  //second group
  let step2;
  for (let y = 350; y < height - 150; y += step2) {
    if (dist(0, y, 0, height / 2) > 100) {
      step2 = 1 + 2 * noise(y * 2);
    } else {
      step2 = 1 + noise(y);
    }
    let a2 = 1 - (dist(0, y, 0, height / 2) / (height - 100)) * 2;
    for (let x = 0; x < width; x++) {
      let w = color(0.5);
      let c2 = lerpColor(
        w,
        bg,
        (dist(x, y, width / 2, y) / width) *
          2 *
          ((height + 300) / dist(x, y, x, -300))
      );
      strokeWeight(0.5);
      c2.setAlpha(a2);
      stroke(c2);
      point(x, y);
    }
  }
}

