// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.5.14/p5.js

var noiseFrequency = 0.1;
var POINT_COUNT = 100;
var diameter;

function setup() {
  createCanvas(500, 500);
  colorMode(HSB, 255);
}

function draw() {
  background(255, map(mouseX, 0, width, 0, 255), 255);
  //background(255);
  noFill();
  rectMode(CENTER);

  var points;
  for (var i = 0; i < POINT_COUNT; i++) {
    diameter = map(noise(i), 0, 1, 450, 1000) / mouseY;

    var ps = placePointsNoise(POINT_COUNT, width, height, noiseFrequency);

    points = relaxPoints(ps, 3, mouseX / 10, mouseY / 100);

    var x = points[i].x;
    var y = points[i].y;

    stroke(255, 255, map(diameter, 450 / mouseY, 1000 / mouseY, 0, 255));
    strokeWeight(diameter / 2);
    line(0, y / 2, width, y / 2);
    line(x, 0, x, height);

    noStroke();
    fill(
      155,
      map(mouseX, 0, width, 0, 255),
      map(diameter, 450 / mouseY, 1500 / mouseY, 0, 255)
    );
    rect(x, y, diameter * noise(frameCount / 30) * 3);
  }

  //noLoop();
}

function placePointsNoise(count, w, h, frequency) {
  var points = [];

  for (var i = 0; i < count; i++) {
    var x = noise(i * frequency, 100) * w;
    var y = noise(100, i * frequency) * h;
    points.push({
      x: x,
      y: y,
    });
  }

  return points;
}

function relaxPoints(points, steps, min_distance, strength) {
  for (var step = 0; step < steps; step++) {
    for (var i = 0; i < points.length; i++) {
      for (var j = 0; j < points.length; j++) {
        if (i == j) continue;
        var p1 = points[i];
        var p2 = points[j];

        // checking distance with dist() is slowish
        // this test (which is faster) can weed out some pairs so we don't need to test them
        // this speeds things up a bit, but this is still a very ineficient way to relax points
        // much faster solutions exist, but this one is easier to follow.
        var quickTest =
          abs(p1.x - p2.x) < min_distance && abs(p1.y - p2.y) < min_distance;

        // too close, move apart
        if (quickTest && dist(p1.x, p1.y, p2.x, p2.y) < min_distance) {
          var v = subtractPoint(p1, p2); // find the vector between two points
          var nV = normalizePoint(v); // scale to 1 px unit size

          p1.x += nV.x * strength;
          p1.y += nV.y * strength;
          p2.x -= nV.x * strength;
          p2.y -= nV.y * strength;
        }

        points[i] = p1;
        points[j] = p2;
      }
    }
  }

  return points;
}

function subtractPoint(p1, p2) {
  return {
    x: p1.x - p2.x,
    y: p1.y - p2.y,
  };
}

// scales point as vector to magnitude of one
function normalizePoint(p) {
  var d = dist(0, 0, p.x, p.y);
  return {
    x: p.x / d,
    y: p.y / d,
  };
}

