function mondrian(x, y, w, h, iterations, buffer) {
  iterations = iterations - 1;

  //Given initial starting position, bisect the square in an abritrary position
  //then also do the same else where
  let firstWidth = random(w);
  let firstHeight = random(h);
  let secondHeight = random(h);

  push();
  strokeWeight(5);
  setColor();
  rect(x, y, firstWidth, firstHeight); //top left
  pop();

  push();
  strokeWeight(5);
  setColor();
  rect(x, y + firstHeight, firstWidth, h - firstHeight); //bottom left
  pop();

  push();
  strokeWeight(5);
  setColor();
  rect(x + firstWidth, y, w - firstWidth, secondHeight); //top right
  pop();

  push();
  strokeWeight(5);
  setColor();
  rect(x + firstWidth, y + secondHeight, w - firstWidth, h - secondHeight); //bottom right
  pop();

  let squareSelect = random();


  //255,0,0 ,/// 255 /// 0,0,255 /// 255,255,0
  //top left
  if (iterations > 0) {

    if (squareSelect > 0.75) {
      mondrian(x, y, firstWidth, firstHeight, iterations, buffer);
    }
    //bottom left
    if (squareSelect > 0.50 && squareSelect < .75) {
      mondrian(x, y + firstHeight, firstWidth, h - firstHeight, iterations, buffer);
    }
    //top right
    if (squareSelect > 0.25 && squareSelect < .50) {
      mondrian(x + firstWidth, y, w - firstWidth, secondHeight, iterations, buffer);
    }
    //bottom right
    if (squareSelect > 0.0 && squareSelect < .25) {
      mondrian(x + firstWidth, y + secondHeight, w - firstWidth, h - secondHeight,
        iterations, buffer);
    }
  }
}

