function drawBranch(length) {
  if (length < 3) {
    return;
  }

  // draw this branch
  let l1 = (length * random(0.6, 1)) / 10;
  strokeWeight(l1);
  myTurtle.turnRight(random(-10, 10));
  myTurtle.moveForward(length);
  myTurtle.turnRight(random(-10, 10));
  myTurtle.moveForward(length * 0.8);

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(40 * random(-0.4, 1));
  let l2 = l1 * random(0.8, 1);
  strokeWeight(l2);
  stroke(80, 255 - l2 * 80, 20);
  //stroke(255 - l2 * 80, 80, 20);
  drawBranch(l2 * 10);

  myTurtle.popState();

  // right child
  myTurtle.pushState();
  myTurtle.turnRight(40 * random(-0.4, 1));
  let l3 = l1 * random(0.8, 1);
  strokeWeight(l3);
  stroke(80, 255 - l3 * 80, 20);
  //stroke(255 - l3 * 80, 80, 20);
  drawBranch(l3 * 10);

  myTurtle.popState();

  // middle child
  myTurtle.pushState();
  myTurtle.turnRight(random(-10, 10));
  let l4 = l1 * random(0.8, 1);
  if (random() > 0.6) {
    strokeWeight(l4);
    stroke(80, 255 - l4 * 80, 20);
    //stroke(255 - l4 * 80, 80, 20);
    drawBranch(l4 * 10);
  }
  myTurtle.popState();
}

