// Two ArrayLists to store the vertices for two shapes
// This example assumes that each shape will have the same
// number of vertices, i.e. the size of each ArrayList will be the same
let circle = [];
let square = [];

// An ArrayList for a third set of vertices, the ones we will be drawing
// in the window
let morph = [];

// This boolean variable will control if we are morphing to a circle or square
let state = false;

function setup() {
  createCanvas(720, 400);
  createP('thicc');
  morphSlider = createSlider(1, 50);

  createP('stroke colors');
  colorPicker = createColorPicker('000000')

  // Create a circle using vectors pointing from center
  for (let angle = 0; angle < 360; angle += 9) {
    // Note we are not starting from 0 in order to match the
    // path of a circle.
    let v = p5.Vector.fromAngle(radians(angle - 135));
    v.mult(100);
    circle.push(v);
    // Let's fill out morph ArrayList with blank PVectors while we are at it
    morph.push(createVector());
  }




  // A square is a bunch of vertices along straight lines
  // Top of square
  for (let x = -50; x < 50; x += 10) {
    square.push(createVector(x, -50));
  }
  // Right side
  for (let y = -50; y < 50; y += 10) {
    square.push(createVector(50, y));
  }
  // Bottom
  for (let x = 50; x > -50; x -= 10) {
    square.push(createVector(x, 50));
  }
  // Left side
  for (let y = 50; y > -50; y -= 10) {
    square.push(createVector(-50, y));
  }
}

function draw() {
  background(5);

  // We will keep how far the vertices are from their target
  let totalDistance = 0;

  // Look at each vertex
  for (let i = 0; i < circle.length; i++) {
    let v1;
    // Are we lerping to the circle or square?
    if (state) {
      v1 = circle[i];
    } else {
      v1 = square[i];
    }
    // Get the vertex we will draw
    let v2 = morph[i];
    // Lerp to the target
    v2.lerp(v1, 0.1);
    // Check how far we are from target
    totalDistance += p5.Vector.dist(v1, v2);
  }

  // If all the vertices are close, switch shape
  if (totalDistance < 0.1) {
    state = !state;
  }

  // Draw relative to center
  translate(width / 2, height / 2);
  strokeWeight(morphSlider.value());
  // Draw a polygon that makes up all the vertices
  beginShape();
  noFill();
  stroke(colorPicker.color());

  morph.forEach(v => {
    vertex(v.x, v.y);
  });
  endShape(CLOSE);
}

// Based on https://www.shadertoy.com/view/wtXfRH

vec3 fcos( vec3 x )
{
    vec3 w = fwidth(x);
    #if 0
    return cos(x) * sin(0.5*w)/(0.5*w);     // filtered-exact
	#else
    return cos(x) * smoothstep(6.28,0.0,w); // filtered-approx
	#endif  
}

vec2 fcos(vec2 x)
{
	vec2 w = fwidth(x);
    return cos(x) * sin(0.5*w)/(0.5*w);
}

vec3 getColor( in vec2 p )
{
    vec3 col = vec3(0.4,0.4,0.4);

    col += 0.12*fcos(6.28318*p.x*  1.1+vec3(+1.0,1.0,0.0));
    col += 0.11*fcos(6.28318*p.y*  3.1+vec3(-1.0,0.0,1.0));
    col += 0.10*fcos(6.28318*p.x*  5.1+vec3(+1.0,1.0,0.0));
    col += 0.09*fcos(6.28318*p.y*  9.1+vec3(-1.0,0.0,1.0));
    col += 0.08*fcos(6.28318*p.x* 17.1+vec3(+1.0,1.0,0.0));
    col += 0.07*fcos(6.28318*p.y* 33.1+vec3(-1.0,0.0,1.0));
    col += 0.06*fcos(6.28318*p.x* 65.1+vec3(+1.0,1.0,0.0));
    col += 0.05*fcos(6.28318*p.y*519.1+vec3(-1.0,0.0,1.0));
    col += 0.04*fcos(6.28318*p.x*27.1+vec3(+1.0,1.0,0.0));
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // coordinates
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    p *= 2.0;
    vec2 q = p;

    // deform
    float a = .55;
    float b = 1.2;
    float c = 0.5;

    vec2 o1 = vec2(1.0, 1.0);
    vec2 o2 = vec2(1.0, 2.0);
    for (int i = 0; i < 16; i++){
        p += clamp(1.0, 0.0, a)*cos(b*p.yx + c*iTime + o1);
        a *= 0.502357;
        b *= 2.02357;
        c += 0.0;

        vec2 ot = o1;
        o1 += o2;
        o2 = ot;
    }

    // base color pattern
    vec3 col = getColor(p*.25);


   fragColor = vec4(col, 21.0);
}

